import React from 'react';
import './form.css'

class Form extends React.Component {
    constructor () {
        super()

    this.state = {
        name: '',
        email: '',
        message: ''
    }

    this.handleChange = this.handleChange.bind(this)

}

handleChange (e) {
    this.setState({[e.target.name]: e.target.value})
}

handleSubmit (e) {
    e.preventDefault()

    const {name, email, message} = this.state
}

    render () {
    return (
        <div>
        <form id="contactForm" method="post" action="./index.php" onSubmit={this.handleSubmit}>
        <label>Name</label>
        <input type="name" id="name" onChange={this.handleChange} placeholder="Enter Your Name Here"></input>

        <label>Email</label>
        <input type="email" id="email" onChange={this.handleChange} placeholder="Enter Your Email Here"></input>

        <label>Message</label>
        <textarea type="message" id="message" onChange={this.handleChange} placeholder="Enter Your Message Here"></textarea>

        <input id="submit" name="submit" type="submit" value="submit"></input>
      </form>

    
    </div>
    );
    }
}


export default Form;
